import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { MagnifyingGlassIcon } from '@heroicons/react/24/outline';

const SearchBar = ({ className = '' }) => {
  const [searchQuery, setSearchQuery] = useState('');
  const navigate = useNavigate();

  const handleSubmit = (e) => {
    e.preventDefault();
    // Navigate to nearby malls page with query parameter
    navigate(`/nearby-malls${searchQuery.trim() ? `?q=${encodeURIComponent(searchQuery.trim())}` : ''}`);
    setSearchQuery('');
  };

  return (
    <form 
      onSubmit={handleSubmit} 
      className={`flex items-center relative ${className}`}
    >
      <input
        type="text"
        placeholder="Find nearest parking..."
        value={searchQuery}
        onChange={(e) => setSearchQuery(e.target.value)}
        className="form-input pr-10 py-1.5 text-sm w-full"
      />
      <button
        type="submit"
        className="absolute right-2 text-gray-400 hover:text-gray-600"
        aria-label="Search"
      >
        <MagnifyingGlassIcon className="h-5 w-5" />
      </button>
    </form>
  );
};

export default SearchBar;
