import React, { useState, useEffect, useRef } from 'react';
import { useParams, useNavigate, useLocation } from 'react-router-dom';
import { MapIcon, ClockIcon, CurrencyRupeeIcon, ArrowPathIcon, MapPinIcon, VideoCameraIcon, EyeIcon } from '@heroicons/react/24/outline';
import { Card, Button, Input, Select, Badge, Loading, Alert, Modal } from '../components/common';
import { toast } from 'react-toastify';
import axios from 'axios';
import { useAuth } from '../context/AuthContext';
import { LoginModal } from '../components/auth';

// Set the base URL for the Python backend API
const API_BASE_URL = '/api'; // Use relative path for API requests to avoid CORS issues

// Add after the API_BASE_URL constant
const HOURLY_RATE = 40; // ₹40 per hour
const REFRESH_INTERVAL = 30000; // Increased to 30 seconds (from 10 seconds)

const ParkingSlotPage = () => {
  const { parkingId } = useParams();
  const navigate = useNavigate();
  const location = useLocation();
  const { isAuthenticated } = useAuth();
  
  // Get parking details from location state if available
  const passedParkingDetails = location.state?.parkingDetails;
  
  const [isLoading, setIsLoading] = useState(true);
  const [parkingDetails, setParkingDetails] = useState(null);
  const [parkingSlots, setParkingSlots] = useState([]);
  const [selectedSlot, setSelectedSlot] = useState(null);
  const [isBookingModalOpen, setIsBookingModalOpen] = useState(false);
  const [isLoginModalOpen, setIsLoginModalOpen] = useState(false);
  const [isAPIConnected, setIsAPIConnected] = useState(false);
  const [videoFeed, setVideoFeed] = useState(null);
  const [showVideo, setShowVideo] = useState(false);
  const [pauseRefresh, setPauseRefresh] = useState(false);
  const [lastRefreshTime, setLastRefreshTime] = useState(null);
  
  // Booking form
  const [bookingForm, setBookingForm] = useState({
    name: '',
    vehicleNumber: '',
    vehicleType: 'car',
    mobileNumber: '',
    paymentMethod: 'upi',
    duration: '1'
  });
  const [bookingComplete, setBookingComplete] = useState(false);
  const [bookingReference, setBookingReference] = useState(null);

  // Function to fetch video feed from the backend
  const fetchVideoFeed = async () => {
    try {
      const response = await axios.get(`${API_BASE_URL}/parking/video/${parkingId}`, {
        responseType: 'blob'
      });
      
      if (response.data) {
        // Create a URL for the video blob
        const videoUrl = URL.createObjectURL(response.data);
        setVideoFeed(videoUrl);
      }
    } catch (error) {
      console.error('Error fetching video feed:', error);
      // If API fails, set a mock video URL
      setVideoFeed('https://sample-videos.com/video123/mp4/720/big_buck_bunny_720p_1mb.mp4');
    }
  };

  // This generates slot data without updating state
  const generateSlotsData = (total, available) => {
    const totalSlots = 69;
    const availableSlots = parseInt(available);
    const occupiedCount = totalSlots - availableSlots;
    
    // Randomly determine which slots are occupied
    const occupiedSlots = new Set();
    while (occupiedSlots.size < occupiedCount) {
      occupiedSlots.add(Math.floor(Math.random() * totalSlots));
    }
    
    return Array.from({ length: totalSlots }, (_, index) => {
      const isOccupied = occupiedSlots.has(index);
      return {
        id: index,
        number: index + 1, // Slot numbers start from 1
        status: isOccupied ? 'occupied' : 'available',
        type: index % 5 === 0 ? 'premium' : 'standard'
      };
    });
  };

  // Generate parking slots and update state
  const generateParkingSlots = (total, available) => {
    const slots = generateSlotsData(total, available);
    setParkingSlots(slots);
  };

  useEffect(() => {
    // Check if we have parking details passed from the previous page
    if (passedParkingDetails) {
      // Use the passed parking details
      setParkingDetails({
        id: passedParkingDetails.id,
        name: passedParkingDetails.name,
        address: passedParkingDetails.address,
        coordinates: passedParkingDetails.coordinates,
        hours: passedParkingDetails.hours,
        operatingTime: passedParkingDetails.hours,
        rates: {
          hourly: passedParkingDetails.hourlyRate,
          daily: `₹${parseInt(passedParkingDetails.hourlyRate.replace('₹', '').replace('/hr', '')) * 5}/day`
        },
        features: passedParkingDetails.features ? passedParkingDetails.features.split(', ') : [],
        totalSpots: 69, // Always set to 69 total spots
        availableSpots: passedParkingDetails.available,
        type: passedParkingDetails.type
      });
      
      // Generate parking slots based on the passed details
      generateParkingSlots(69, passedParkingDetails.available);
      
      setIsLoading(false);
    } else {
      // Fallback to fetching parking details
      fetchParkingDetails();
    }

    // Attempt to load the video feed
    fetchVideoFeed();

    // Set up interval to periodically refresh data with longer interval
    const intervalId = setInterval(() => {
      // Only refresh if not paused (user is not in booking process)
      if (!pauseRefresh && !passedParkingDetails) {
        fetchLiveData();
        setLastRefreshTime(new Date().toLocaleTimeString());
      }
    }, REFRESH_INTERVAL);

    // Clean up interval on unmount
    return () => clearInterval(intervalId);
  }, [parkingId, passedParkingDetails, pauseRefresh]);

  // Effect to pause refresh when booking modal is open
  useEffect(() => {
    setPauseRefresh(isBookingModalOpen);
  }, [isBookingModalOpen]);

  // Function to handle slot updates when live data comes in
  const updateSlots = (newSlots) => {
    if (selectedSlot) {
      // Check if the selected slot is still available in the new data
      const selectedSlotInNewData = newSlots.find(slot => slot.id === selectedSlot.id);
      
      if (selectedSlotInNewData && selectedSlotInNewData.status !== 'available') {
        // If selected slot is no longer available, notify the user
        toast.warning("Your selected slot has been taken. Please select another slot.", {
          autoClose: 5000
        });
        setSelectedSlot(null);
      }
    }
    
    // Update the slots
    setParkingSlots(newSlots);
  };

  const handleSlotSelect = (slot) => {
    if (slot.status === 'available') {
      setSelectedSlot(slot);
      // Pause automatic refresh when a slot is selected
      setPauseRefresh(true);
    } else {
      toast.error("This slot is already occupied");
    }
  };

  const fetchLiveData = async () => {
    // Don't fetch if booking modal is open
    if (isBookingModalOpen) return;
    
    try {
      // Try to connect to the Python backend API for real-time video processing
      const response = await axios.get(`${API_BASE_URL}/parking/spaces`, { 
        params: { 
          parkingId: parkingDetails?.id // Send the specific parking ID to get data for that location
        } 
      });
      
      if (response.data) {
        setIsAPIConnected(true);
        console.log('Raw API response:', response.data); // Log the raw response for debugging
        
        // Update available spots count while preserving total as 69
        if (parkingDetails) {
          setParkingDetails(prev => ({
            ...prev,
            availableSpots: response.data.available,
            totalSpots: response.data.total || 69 // Use total from API if available
          }));
        }
        
        // Transform API data to match our slot format
        if (response.data.spaces && Object.keys(response.data.spaces).length > 0) {
          // If real spaces data is provided, convert from object to array
          const apiSlots = Object.entries(response.data.spaces).map(([id, space]) => ({
            id: parseInt(id),
            number: parseInt(id) + 1, // Slot numbers start from 1
            status: space.status === 'available' ? 'available' : 'occupied',
            type: parseInt(id) % 5 === 0 ? 'premium' : 'standard'
          }));
          
          console.log('Transformed API slots:', apiSlots); // Log for debugging
          updateSlots(apiSlots);
        } else {
          // If only count is provided, create slots with random occupied/available
          console.log('Using counts to generate random slots: total=', response.data.total, 'available=', response.data.available);
          const newSlots = generateSlotsData(response.data.total || 69, response.data.available);
          updateSlots(newSlots);
        }
        
        setIsLoading(false);
      }
    } catch (error) {
      console.error('Error fetching live parking data from video system:', error);
      
      // If API call fails, use mock data as fallback
      if (!isAPIConnected) {
        console.log('Falling back to mock data');
        const mockSlots = Array.from({ length: 69 }, (_, index) => {
          // Use a deterministic pattern for occupied slots
          const isOccupied = [3, 7, 10, 14, 19, 20, 22, 25, 31, 33, 39, 42, 44, 47, 50, 52, 56, 58, 60, 63, 67].includes(index);
          return {
            id: index,
            number: index + 1,
            status: isOccupied ? 'occupied' : 'available',
            type: index % 5 === 0 ? 'premium' : 'standard'
          };
        });
        
        updateSlots(mockSlots);
        
        // Update parking details to show correct number of available spots
        if (parkingDetails) {
          const availableSpots = mockSlots.filter(slot => slot.status === 'available').length;
          setParkingDetails(prev => ({
            ...prev,
            availableSpots: availableSpots,
            totalSpots: 69 // Always 69 total
          }));
        }
        
        setIsLoading(false);
      }
    }
  };

  const fetchParkingDetails = () => {
    // Simulate fetching parking details
    setTimeout(() => {
      const mockParkingDetails = {
        id: parkingId,
        name: "R City Mall Parking",
        address: "LBS Marg, Ghatkopar West, Mumbai",
        coordinates: [19.0948, 72.9201],
        hours: "Open 24 hours",
        operatingTime: "24/7 operation",
        rates: {
          hourly: `₹${HOURLY_RATE}/hr`,
          daily: `₹${HOURLY_RATE * 5}/day`
        },
        features: ["CCTV", "Security", "Covered Parking", "EV Charging"],
        totalSpots: 69,
        availableSpots: 32,
        type: "mall"
      };
      
      setParkingDetails(mockParkingDetails);
      
      // Generate parking slots with random available/occupied status
      generateParkingSlots(69, 32);
      
      setIsLoading(false);
      
      // After setting basic details, try to fetch live data
      fetchLiveData();
    }, 1000);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setBookingForm(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleBookClick = () => {
    if (!isAuthenticated) {
      setIsLoginModalOpen(true);
      return;
    }
    
    setIsBookingModalOpen(true);
  };

  const handleGetDirections = () => {
    // In a real app, this would open Google Maps with directions
    window.open(`https://www.google.com/maps/search/?api=1&query=${parkingDetails.coordinates[0]},${parkingDetails.coordinates[1]}`, '_blank');
  };

  const downloadTicket = () => {
    // In a real app, this would generate and download a PDF ticket
    toast.info("Downloading parking ticket...");
    setTimeout(() => {
      toast.success("Ticket downloaded successfully!");
    }, 1500);
  };

  const validateForm = () => {
    const { name, vehicleNumber, mobileNumber } = bookingForm;
    
    if (!name.trim()) {
      toast.error("Please enter your name");
      return false;
    }
    
    if (!vehicleNumber.trim()) {
      toast.error("Please enter your vehicle number");
      return false;
    }
    
    if (!mobileNumber.trim() || mobileNumber.length !== 10) {
      toast.error("Please enter a valid 10-digit mobile number");
      return false;
    }
    
    return true;
  };

  const handleBooking = () => {
    if (!validateForm()) {
      return;
    }
    
    setIsLoading(true);
    
    // Simulate API call to book parking
    setTimeout(() => {
      // Generate a random reference number
      const randomRef = Math.random().toString(36).substring(2, 8).toUpperCase();
      setBookingReference(`SPOT${randomRef}`);
      
      setBookingComplete(true);
      setIsLoading(false);
      
      // Update the parking slot status to booked
      if (selectedSlot) {
        const updatedSlots = parkingSlots.map(slot => {
          if (slot.id === selectedSlot.id) {
            return { ...slot, status: 'occupied' };
          }
          return slot;
        });
        
        setParkingSlots(updatedSlots);
        
        // Update available count
        if (parkingDetails) {
          setParkingDetails(prev => ({
            ...prev,
            availableSpots: prev.availableSpots - 1
          }));
        }
      }
    }, 2000);
  };

  // Add to renderContent
  const refreshParkingData = () => {
    setIsLoading(true);
    
    // In a real app with the API, we would force a refresh of the video frame
    if (isAPIConnected) {
      axios.post(`${API_BASE_URL}/parking/refresh`)
        .then(() => {
          fetchLiveData();
          setLastRefreshTime(new Date().toLocaleTimeString());
          toast.success("Parking data refreshed!");
        })
        .catch(error => {
          console.error('Error refreshing parking data:', error);
          fetchLiveData(); // Try to fetch anyway
          setLastRefreshTime(new Date().toLocaleTimeString());
          toast.error("Failed to refresh data. Using cached information.");
        });
    } else {
      // Fall back to mock refresh
      fetchLiveData();
      setLastRefreshTime(new Date().toLocaleTimeString());
    }
  };

  const toggleVideoFeed = () => {
    setShowVideo(!showVideo);
  };

  if (isLoading && !parkingDetails) {
    return (
      <div className="flex flex-col items-center justify-center h-[70vh]">
        <Loading size="lg" />
        <p className="mt-4 text-neutral-600 dark:text-neutral-400">Loading parking details...</p>
      </div>
    );
  }

  return (
    <div className="max-w-4xl mx-auto">
      {/* Header */}
      <div className="bg-yellow-400 rounded-lg p-6 mb-6">
        <h1 className="text-2xl font-bold text-center">SpotSense</h1>
        <p className="text-center text-black mt-1 text-sm">Select and book your exact parking spot</p>
        {isAPIConnected && (
          <div className="flex items-center justify-center mt-2">
            <span className="inline-block h-2 w-2 rounded-full bg-green-500 mr-2"></span>
            <span className="text-xs text-black">Live data connected</span>
            {lastRefreshTime && (
              <span className="text-xs text-black ml-4">Last updated: {lastRefreshTime}</span>
            )}
          </div>
        )}
      </div>
      
      {/* Video Feed Toggle Button */}
      <div className="flex justify-end mb-4">
        <Button
          variant="outline"
          size="sm"
          leftIcon={<VideoCameraIcon className="h-4 w-4" />}
          onClick={toggleVideoFeed}
          className="border-yellow-500 text-yellow-600"
        >
          {showVideo ? "Hide Video Feed" : "Show Video Feed"}
        </Button>
      </div>
      
      {/* Video Feed Display */}
      {showVideo && videoFeed && (
        <Card 
          title="" 
          subtitle="" 
          footer="" 
          image="" 
          onClick={() => {}}
          className="mb-6 overflow-hidden"
        >
          <div className="relative pt-[56.25%]"> {/* 16:9 aspect ratio */}
            <video 
              className="absolute top-0 left-0 w-full h-full object-cover"
              src={videoFeed}
              autoPlay
              muted
              loop
            />
            <div className="absolute top-2 right-2 bg-black/50 text-white text-xs px-2 py-1 rounded-full flex items-center">
              <EyeIcon className="h-3 w-3 mr-1" />
              <span>Live Feed</span>
            </div>
          </div>
        </Card>
      )}
      
      {/* Parking Details */}
      <div className="mb-6">
        <div className="flex items-center mb-4">
          <Button 
            variant="outline" 
            size="sm" 
            onClick={() => navigate('/')}
            className="mr-4"
          >
            Back to List
          </Button>
          <Button 
            variant="outline" 
            size="sm" 
            onClick={() => navigate(`/parking-detail/${parkingId}`)}
            className="text-yellow-500 border-yellow-500"
          >
            View Parking Details
          </Button>
        </div>
        
        <Card 
          title="" 
          subtitle="" 
          footer="" 
          image="" 
          onClick={() => {}}
          className="mb-6 border-none shadow-md overflow-hidden"
        >
          <div className="border-l-4 border-yellow-400 pl-4">
            <div>
              <div className="flex items-start mb-2">
                <MapPinIcon className="h-5 w-5 text-yellow-500 flex-shrink-0 mt-0.5 mr-2" />
                <div>
                  <h2 className="font-semibold text-lg">{parkingDetails?.name}</h2>
                  <p className="text-neutral-600 dark:text-neutral-400 text-sm">{parkingDetails?.address}</p>
                </div>
              </div>
              
              <div className="flex items-start mb-2">
                <ClockIcon className="h-5 w-5 text-yellow-500 flex-shrink-0 mt-0.5 mr-2" />
                <div>
                  <p className="text-neutral-600 dark:text-neutral-400 text-sm">{parkingDetails?.hours}</p>
                  <p className="text-neutral-600 dark:text-neutral-400 text-sm">{parkingDetails?.operatingTime}</p>
                </div>
              </div>
            </div>
          </div>
        </Card>
        
        {/* Parking availability */}
        <div className="bg-yellow-100 p-4 rounded-lg mb-6">
          <p className="font-medium text-center">Parking Availability: [{parkingDetails?.availableSpots}/{parkingDetails?.totalSpots}]</p>
          <div className="flex items-center justify-center mt-1">
            <span className="text-xs text-gray-500">
              {pauseRefresh 
                ? "Auto-refresh paused during slot selection" 
                : `Auto-refreshes every ${REFRESH_INTERVAL/1000} seconds`
              }
            </span>
            <Button
              variant="ghost"
              size="xs"
              leftIcon={<ArrowPathIcon className="h-3 w-3" />}
              onClick={refreshParkingData}
              className="ml-2"
            >
              Refresh Now
            </Button>
          </div>
        </div>
      </div>

      {/* Parking Slots Section */}
      <div className="mb-8">
        <div className="flex justify-between items-center mb-4">
          <h2 className="text-lg font-semibold">Select a Parking Slot</h2>
          <Button 
            size="sm" 
            variant="outline" 
            leftIcon={<ArrowPathIcon className="h-4 w-4" />}
            onClick={refreshParkingData}
          >
            Refresh
          </Button>
        </div>
        
        {isLoading ? (
          <div className="flex justify-center py-8">
            <Loading />
          </div>
        ) : (
          <>
            {/* Parking slot grid */}
            <div className="grid grid-cols-5 md:grid-cols-10 gap-1 mb-6 bg-yellow-50 p-3 rounded-lg">
              {parkingSlots.map((slot) => (
                <div
                  key={slot.id}
                  className={`
                    h-10 rounded flex items-center justify-center text-sm font-medium 
                    ${slot.status === 'available' 
                      ? selectedSlot && selectedSlot.id === slot.id
                        ? 'bg-yellow-200 text-black cursor-pointer'
                        : 'bg-white border border-gray-300 hover:bg-yellow-100 cursor-pointer'
                      : 'bg-black text-white cursor-not-allowed'
                    }
                  `}
                  onClick={() => handleSlotSelect(slot)}
                >
                  {slot.number}
                </div>
              ))}
            </div>
            
            {/* Selected slot info and book button */}
            <div className="flex justify-between items-center">
              <div>
                {selectedSlot ? (
                  <p className="font-medium">
                    Selected Slot: <span className="text-yellow-600">{selectedSlot.number}</span>
                  </p>
                ) : (
                  <p className="text-neutral-600 dark:text-neutral-400">No slot selected</p>
                )}
              </div>
              <Button 
                variant="primary"
                disabled={!selectedSlot}
                onClick={handleBookClick}
                className="bg-yellow-400 hover:bg-yellow-500 border-yellow-500 text-black"
              >
                Book Selected Slot
              </Button>
            </div>
          </>
        )}
      </div>

      {/* Booking Modal */}
      <Modal
        isOpen={isBookingModalOpen}
        onClose={() => {
          if (!bookingComplete) {
            setIsBookingModalOpen(false);
            setPauseRefresh(false); // Resume auto-refresh when modal is closed
          }
        }}
        title={bookingComplete ? "Booking Confirmed!" : "Book Parking Slot"}
        size="lg"
        footer={
          bookingComplete ? (
            <div className="flex gap-2 justify-end">
              <Button variant="outline" onClick={() => {
                setIsBookingModalOpen(false);
                setBookingComplete(false);
                setSelectedSlot(null);
                setPauseRefresh(false); // Resume auto-refresh
                fetchLiveData();
              }}>
                Back to Parking
              </Button>
              <Button onClick={downloadTicket}>
                Download Ticket
              </Button>
              <Button 
                variant="primary" 
                onClick={handleGetDirections}
                className="bg-yellow-400 hover:bg-yellow-500 border-yellow-500 text-black"
              >
                Get Directions
              </Button>
            </div>
          ) : (
            <div className="flex gap-2 justify-end">
              <Button variant="outline" onClick={() => {
                setIsBookingModalOpen(false);
                setPauseRefresh(false); // Resume auto-refresh
              }}>
                Cancel
              </Button>
              <Button 
                variant="primary" 
                onClick={handleBooking}
                isLoading={isLoading}
                className="bg-yellow-400 hover:bg-yellow-500 border-yellow-500 text-black"
              >
                Confirm Booking
              </Button>
            </div>
          )
        }
      >
        {bookingComplete ? (
          <div className="text-center py-4">
            <div className="mb-4 text-yellow-500">
              <svg 
                className="h-16 w-16 mx-auto" 
                fill="currentColor" 
                viewBox="0 0 20 20" 
                xmlns="http://www.w3.org/2000/svg"
              >
                <path 
                  fillRule="evenodd" 
                  d="M10 18a8 8 0 100-16 8 8 0 000 16zm3.707-9.293a1 1 0 00-1.414-1.414L9 10.586 7.707 9.293a1 1 0 00-1.414 1.414l2 2a1 1 0 001.414 0l4-4z" 
                  clipRule="evenodd" 
                />
              </svg>
            </div>
            
            <h3 className="text-xl font-bold mb-4">Your parking has been booked!</h3>
            
            <div className="bg-yellow-50 p-4 rounded-lg mb-6">
              <table className="w-full text-left" id="ticket-content">
                <tbody>
                  <tr>
                    <td className="py-1 font-medium">Booking Reference:</td>
                    <td className="py-1">{bookingReference}</td>
                  </tr>
                  <tr>
                    <td className="py-1 font-medium">Name:</td>
                    <td className="py-1">{bookingForm.name}</td>
                  </tr>
                  <tr>
                    <td className="py-1 font-medium">Vehicle Number:</td>
                    <td className="py-1">{bookingForm.vehicleNumber}</td>
                  </tr>
                  <tr>
                    <td className="py-1 font-medium">Parking Spot:</td>
                    <td className="py-1">Slot {selectedSlot?.number}</td>
                  </tr>
                  <tr>
                    <td className="py-1 font-medium">Duration:</td>
                    <td className="py-1">{bookingForm.duration} {parseInt(bookingForm.duration) === 1 ? 'hour' : 'hours'}</td>
                  </tr>
                  <tr>
                    <td className="py-1 font-medium">Payment Method:</td>
                    <td className="py-1">
                      {bookingForm.paymentMethod === 'upi' ? 'UPI' : 
                       bookingForm.paymentMethod === 'card' ? 'Credit/Debit Card' : 'Cash'}
                    </td>
                  </tr>
                  <tr>
                    <td className="py-1 font-medium">Date & Time:</td>
                    <td className="py-1">{new Date().toLocaleString()}</td>
                  </tr>
                  <tr>
                    <td className="py-1 font-medium">Rate:</td>
                    <td className="py-1">{parkingDetails?.rates?.hourly}</td>
                  </tr>
                </tbody>
              </table>
            </div>
            
            <p className="text-sm text-neutral-600 dark:text-neutral-400 mb-2">
              A confirmation has been sent to your mobile number: {bookingForm.mobileNumber}
            </p>
          </div>
        ) : (
          <div>
            {isLoading ? (
              <div className="flex justify-center py-8">
                <Loading />
              </div>
            ) : (
              <div>
                <div className="bg-yellow-50 p-3 rounded-lg mb-4">
                  <div className="flex justify-between">
                    <div>
                      <p className="font-medium">{parkingDetails?.name}</p>
                      <p className="text-sm text-neutral-600 dark:text-neutral-400">Slot {selectedSlot?.number}</p>
                    </div>
                    <div className="text-right">
                      <p className="font-medium">{parkingDetails?.rates?.hourly}</p>
                    </div>
                  </div>
                </div>
                
                <div className="space-y-4">
                  <Input
                    label="Full Name"
                    id="name"
                    name="name"
                    value={bookingForm.name}
                    onChange={handleInputChange}
                    placeholder="Enter your full name"
                    required
                  />
                  
                  <Input
                    label="Vehicle Number"
                    id="vehicleNumber"
                    name="vehicleNumber"
                    value={bookingForm.vehicleNumber}
                    onChange={handleInputChange}
                    placeholder="Enter vehicle number (e.g. MH00SS0000)"
                    required
                  />
                  
                  <Select
                    label="Vehicle Type"
                    id="vehicleType"
                    name="vehicleType"
                    value={bookingForm.vehicleType}
                    onChange={handleInputChange}
                    options={[
                      { value: 'car', label: 'Car' },
                      { value: 'bike', label: 'Bike' },
                      { value: 'suv', label: 'SUV' }
                    ]}
                  />
                  
                  <Input
                    label="Mobile Number"
                    id="mobileNumber"
                    name="mobileNumber"
                    value={bookingForm.mobileNumber}
                    onChange={handleInputChange}
                    placeholder="Enter your 10-digit mobile number"
                    required
                  />
                  
                  <Select
                    label="Duration"
                    id="duration"
                    name="duration"
                    value={bookingForm.duration}
                    onChange={handleInputChange}
                    options={[
                      { value: '1', label: '1 Hour' },
                      { value: '2', label: '2 Hours' },
                      { value: '3', label: '3 Hours' },
                      { value: '4', label: '4 Hours' },
                      { value: '5', label: '5 Hours' },
                      { value: '24', label: 'Full Day (24 Hours)' }
                    ]}
                  />
                  
                  <div>
                    <label className="form-label">Payment Method</label>
                    <div className="space-y-2">
                      <div className="flex items-center">
                        <input
                          type="radio"
                          id="upi"
                          name="paymentMethod"
                          value="upi"
                          checked={bookingForm.paymentMethod === 'upi'}
                          onChange={handleInputChange}
                          className="mr-2"
                        />
                        <label htmlFor="upi">UPI</label>
                      </div>
                      <div className="flex items-center">
                        <input
                          type="radio"
                          id="card"
                          name="paymentMethod"
                          value="card"
                          checked={bookingForm.paymentMethod === 'card'}
                          onChange={handleInputChange}
                          className="mr-2"
                        />
                        <label htmlFor="card">Credit/Debit Card</label>
                      </div>
                      <div className="flex items-center">
                        <input
                          type="radio"
                          id="cash"
                          name="paymentMethod"
                          value="cash"
                          checked={bookingForm.paymentMethod === 'cash'}
                          onChange={handleInputChange}
                          className="mr-2"
                        />
                        <label htmlFor="cash">Cash</label>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            )}
          </div>
        )}
      </Modal>
      
      {/* Login Modal */}
      <LoginModal 
        isOpen={isLoginModalOpen} 
        onClose={() => {
          setIsLoginModalOpen(false);
          setPauseRefresh(false); // Resume auto-refresh
        }}
        onSignupClick={() => setIsLoginModalOpen(false)}
      />
      
      {/* Mobile navigation dots */}
      <div className="flex justify-center space-x-2 mt-8">
        <div className="w-2 h-2 rounded-full bg-gray-300"></div>
        <div className="w-2 h-2 rounded-full bg-yellow-400"></div>
        <div className="w-2 h-2 rounded-full bg-gray-300"></div>
      </div>
    </div>
  );
};

export default ParkingSlotPage;
