import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { 
  MapPinIcon, 
  ArrowRightIcon, 
  MagnifyingGlassIcon,
  BuildingStorefrontIcon
} from '@heroicons/react/24/outline';
import { Card, Button, Loading, Input } from '../components/common';
import axios from 'axios';

const libraries = ['places'];

const NearbyMallsPage = () => {
  const navigate = useNavigate();
  const [currentLocation, setCurrentLocation] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const [nearbyMalls, setNearbyMalls] = useState([]);
  const [selectedMall, setSelectedMall] = useState(null);
  const [searchRadius, setSearchRadius] = useState(3000); // 3km default radius
  const [searchTerm, setSearchTerm] = useState('');
  
  useEffect(() => {
    // Get user's current location
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          const location = {
            lat: position.coords.latitude,
            lng: position.coords.longitude,
          };
          setCurrentLocation(location);
          setIsLoading(false);
          
          // Once we have location, search for nearby malls
          searchNearbyMalls(location, searchRadius);
        },
        (error) => {
          console.error('Error getting location:', error);
          // Default to Mumbai location
          const defaultLocation = { lat: 19.0760, lng: 72.8777 };
          setCurrentLocation(defaultLocation);
          setIsLoading(false);
          
          // Search with default location
          searchNearbyMalls(defaultLocation, searchRadius);
        }
      );
    } else {
      console.warn('Geolocation is not supported by this browser');
      // Default to Mumbai location
      const defaultLocation = { lat: 19.0760, lng: 72.8777 };
      setCurrentLocation(defaultLocation);
      setIsLoading(false);
    }
  }, []);

  const searchNearbyMalls = async (location, radius) => {
    setIsLoading(true);
    
    try {
      // In a real app, this would call the Google Places API
      // For this demo, we'll use mock data (but the structure matches the Places API)
      
      // Simulate API delay
      await new Promise(resolve => setTimeout(resolve, 1500));
      
      // Mock data to simulate mall parking spots
      const mockMalls = [
        {
          place_id: 'mall1',
          name: 'Phoenix Marketcity',
          vicinity: 'LBS Marg, Kurla West, Mumbai',
          geometry: {
            location: { lat: location.lat + 0.01, lng: location.lng + 0.02 }
          },
          photos: [{ photo_reference: 'phoenix' }],
          rating: 4.5,
          user_ratings_total: 12500,
          parking: {
            available: 120,
            total: 250,
            hourly_rate: '₹60'
          },
          distance: '1.2 km'
        },
        {
          place_id: 'mall2',
          name: 'R City Mall',
          vicinity: 'LBS Marg, Ghatkopar West, Mumbai',
          geometry: {
            location: { lat: location.lat - 0.008, lng: location.lng + 0.01 }
          },
          photos: [{ photo_reference: 'rcity' }],
          rating: 4.3,
          user_ratings_total: 8700,
          parking: {
            available: 75,
            total: 180,
            hourly_rate: '₹50'
          },
          distance: '2.5 km'
        },
        {
          place_id: 'mall3',
          name: 'Infiniti Mall',
          vicinity: 'New Link Road, Malad West, Mumbai',
          geometry: {
            location: { lat: location.lat + 0.015, lng: location.lng - 0.01 }
          },
          photos: [{ photo_reference: 'infiniti' }],
          rating: 4.2,
          user_ratings_total: 9300,
          parking: {
            available: 95,
            total: 200,
            hourly_rate: '₹40'
          },
          distance: '3.1 km'
        },
        {
          place_id: 'mall4',
          name: 'Oberoi Mall',
          vicinity: 'Western Express Highway, Goregaon East, Mumbai',
          geometry: {
            location: { lat: location.lat - 0.012, lng: location.lng - 0.015 }
          },
          photos: [{ photo_reference: 'oberoi' }],
          rating: 4.4,
          user_ratings_total: 10200,
          parking: {
            available: 110,
            total: 220,
            hourly_rate: '₹70'
          },
          distance: '4.0 km'
        },
        {
          place_id: 'mall5',
          name: 'Inorbit Mall',
          vicinity: 'Link Road, Malad West, Mumbai',
          geometry: {
            location: { lat: location.lat + 0.005, lng: location.lng - 0.02 }
          },
          photos: [{ photo_reference: 'inorbit' }],
          rating: 4.1,
          user_ratings_total: 7800,
          parking: {
            available: 85,
            total: 175,
            hourly_rate: '₹50'
          },
          distance: '4.8 km'
        }
      ];
      
      // Filter by search term if one exists
      let filteredMalls = mockMalls;
      if (searchTerm) {
        filteredMalls = mockMalls.filter(mall => 
          mall.name.toLowerCase().includes(searchTerm.toLowerCase())
        );
      }
      
      setNearbyMalls(filteredMalls);
    } catch (error) {
      console.error('Error searching for nearby malls:', error);
    } finally {
      setIsLoading(false);
    }
  };

  const handleRadiusChange = (e) => {
    const radius = parseInt(e.target.value);
    setSearchRadius(radius);
    if (currentLocation) {
      searchNearbyMalls(currentLocation, radius);
    }
  };

  const handleSearch = (e) => {
    e.preventDefault();
    if (currentLocation) {
      searchNearbyMalls(currentLocation, searchRadius);
    }
  };

  const handleBookParking = (mall) => {
    // In a real app, this would navigate to a booking page for this specific mall
    // For now, we'll just use our generic parking booking flow
    const parkingId = mall.place_id.replace('mall', 'p');
    navigate(`/parking/${parkingId}`);
  };

  const getPhotoUrl = (photoReference) => {
    // In a real app, this would use the Places API photo endpoint
    // For now, return placeholder images based on the reference
    const placeholders = {
      'phoenix': 'https://images.unsplash.com/photo-1557775604-9b8fbaa51512?ixlib=rb-4.0.3&auto=format&fit=crop&w=800&q=60',
      'rcity': 'https://images.unsplash.com/photo-1519567241313-8b1e156c7838?ixlib=rb-4.0.3&auto=format&fit=crop&w=800&q=60',
      'infiniti': 'https://images.unsplash.com/photo-1581417478175-a9ef18f210c2?ixlib=rb-4.0.3&auto=format&fit=crop&w=800&q=60',
      'oberoi': 'https://images.unsplash.com/photo-1568454537842-d933259bb258?ixlib=rb-4.0.3&auto=format&fit=crop&w=800&q=60',
      'inorbit': 'https://images.unsplash.com/photo-1441986300917-64674bd600d8?ixlib=rb-4.0.3&auto=format&fit=crop&w=800&q=60'
    };
    
    return placeholders[photoReference] || 'https://images.unsplash.com/photo-1555448248-2571daf6344b?ixlib=rb-4.0.3&auto=format&fit=crop&w=800&q=60';
  };

  return (
    <div className="container mx-auto px-4 py-8">
      <div className="bg-yellow-400 rounded-lg p-6 mb-6">
        <h1 className="text-2xl font-bold text-center">SpotSense</h1>
        <p className="text-center text-black mt-1 text-sm">Find parking spots at malls near you</p>
      </div>
      
      <div className="flex justify-between items-center mb-6">
        <button 
          onClick={() => navigate('/')}
          className="flex items-center text-neutral-700 dark:text-neutral-300 hover:text-yellow-500 dark:hover:text-yellow-400"
        >
          <ArrowRightIcon className="h-4 w-4 mr-1 rotate-180" />
          Back to home
        </button>
      </div>
      
      {/* Search form */}
      <div className="bg-white rounded-lg shadow-md p-4 mb-6">
        <form onSubmit={handleSearch} className="flex flex-wrap gap-4">
          <div className="flex-grow min-w-[200px]">
            <label htmlFor="search" className="block text-sm font-medium text-gray-700 mb-1">
              Search for malls
            </label>
            <div className="relative">
              <input
                type="text"
                id="search"
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                placeholder="Enter mall name"
                className="w-full p-2 pl-10 border border-gray-300 rounded-md focus:ring-2 focus:ring-yellow-400 focus:border-transparent"
              />
              <MagnifyingGlassIcon className="absolute left-3 top-2.5 h-5 w-5 text-gray-400" />
            </div>
          </div>
          
          <div className="w-full sm:w-auto">
            <label htmlFor="radius" className="block text-sm font-medium text-gray-700 mb-1">
              Search radius
            </label>
            <select
              id="radius"
              value={searchRadius}
              onChange={handleRadiusChange}
              className="w-full p-2 border border-gray-300 rounded-md focus:ring-2 focus:ring-yellow-400 focus:border-transparent"
            >
              <option value="1000">1 km</option>
              <option value="3000">3 km</option>
              <option value="5000">5 km</option>
              <option value="10000">10 km</option>
            </select>
          </div>
          
          <div className="w-full sm:w-auto flex items-end">
            <button
              type="submit"
              className="w-full sm:w-auto bg-yellow-400 hover:bg-yellow-500 text-black font-medium py-2 px-4 rounded"
            >
              Search
            </button>
          </div>
        </form>
      </div>
      
      {/* Static Mall Image Banner instead of Google Maps */}
      <div className="mb-6 h-[250px] rounded-lg overflow-hidden shadow-md relative">
        <img 
          src="https://images.unsplash.com/photo-1519567241313-8b1e156c7838?ixlib=rb-4.0.3&auto=format&fit=crop&w=1200&q=80" 
          alt="Mall Shopping"
          className="w-full h-full object-cover"
        />
        <div className="absolute inset-0 bg-black bg-opacity-40 flex flex-col items-center justify-center text-white p-6">
          <h2 className="text-2xl font-bold mb-2 text-center">Find Mall Parking Near You</h2>
          <p className="text-center max-w-lg">
            Discover available parking spots at popular shopping malls and book instantly with SpotSense
          </p>
        </div>
      </div>
      
      {/* Mall listing */}
      <h2 className="text-xl font-semibold mb-4">Nearby Mall Parking</h2>
      
      {isLoading ? (
        <div className="flex justify-center py-8">
          <Loading />
        </div>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {nearbyMalls.length === 0 ? (
            <div className="col-span-full text-center py-10">
              <div className="text-yellow-500 mb-4">
                <BuildingStorefrontIcon className="h-16 w-16 mx-auto" />
              </div>
              <h3 className="text-xl font-semibold mb-2">No malls found</h3>
              <p className="text-neutral-600 dark:text-neutral-400 mb-6 max-w-md mx-auto">
                We couldn't find any malls matching your search criteria. Try increasing the search radius or modifying your search term.
              </p>
              <Button 
                onClick={() => {
                  setSearchTerm('');
                  if (currentLocation) {
                    searchNearbyMalls(currentLocation, 5000);
                  }
                }}
                className="bg-yellow-400 hover:bg-yellow-500 text-black"
              >
                Reset Search
              </Button>
            </div>
          ) : (
            nearbyMalls.map((mall) => (
              <Card key={mall.place_id} className="overflow-hidden" title="" subtitle="" footer={null} image={null} onClick={() => {}}>
                <div className="h-40 overflow-hidden">
                  <img
                    src={getPhotoUrl(mall.photos[0].photo_reference)}
                    alt={mall.name}
                    className="w-full h-full object-cover"
                  />
                </div>
                
                <div className="p-4">
                  <h3 className="font-semibold text-lg mb-1">{mall.name}</h3>
                  
                  <div className="flex items-start mb-3">
                    <MapPinIcon className="h-5 w-5 text-neutral-500 flex-shrink-0 mt-0.5 mr-2" />
                    <p className="text-neutral-600 dark:text-neutral-400 text-sm">
                      {mall.vicinity} <span className="text-xs font-medium ml-1">({mall.distance})</span>
                    </p>
                  </div>
                  
                  <div className="flex justify-between mb-3">
                    <div className="flex items-center text-sm">
                      <BuildingStorefrontIcon className="h-4 w-4 text-yellow-500 mr-1" />
                      <span>
                        <span className="font-medium">{mall.rating}</span>
                        <span className="text-neutral-500 text-xs"> ({mall.user_ratings_total})</span>
                      </span>
                    </div>
                    
                    <div className="text-sm font-medium text-yellow-600">
                      {mall.parking.hourly_rate}/hr
                    </div>
                  </div>
                  
                  <div className="mb-4">
                    <div className="flex justify-between text-sm mb-1">
                      <span className="text-neutral-600">Parking spots available:</span>
                      <span className="font-medium">
                        <span className="text-green-600">{mall.parking.available}</span>
                        /{mall.parking.total}
                      </span>
                    </div>
                    
                    <div className="w-full bg-neutral-200 rounded-full h-2">
                      <div
                        className="bg-green-500 h-2 rounded-full"
                        style={{ width: `${(mall.parking.available / mall.parking.total) * 100}%` }}
                      />
                    </div>
                  </div>
                  
                  <Button
                    fullWidth
                    onClick={() => handleBookParking(mall)}
                    className="bg-yellow-400 hover:bg-yellow-500 text-black"
                  >
                    Book Parking
                  </Button>
                </div>
              </Card>
            ))
          )}
        </div>
      )}
      
      {/* Mobile navigation dots */}
      <div className="flex justify-center space-x-2 mt-8">
        <div className="w-2 h-2 rounded-full bg-gray-300"></div>
        <div className="w-2 h-2 rounded-full bg-yellow-400"></div>
        <div className="w-2 h-2 rounded-full bg-gray-300"></div>
      </div>
    </div>
  );
};

export default NearbyMallsPage;
